from rest_framework.response import Responsefrom rest_framework.decorators import api_viewfrom drf_yasg.utils import swagger_auto_schemafrom accounts.models import PrivacyModelfrom api.v1.account.serializers import PrivacySerializer@swagger_auto_schema(method='get', tags=["privacy"])@api_view(["GET"])def get_privacy(request):    privacy = PrivacyModel.objects.all().first()    serializer = PrivacySerializer(privacy)    return Response(serializer.data)