from rest_framework import serializersfrom accounts.models import CountryModelfrom apps.ad.models import *from api.v1.account.serializers import CountrySerializerclass AdPictureSerializer(serializers.ModelSerializer):    class Meta:        model = AdPictureModel        exclude = ['ad']class AdSerializer(serializers.ModelSerializer):    picture = AdPictureSerializer(many=True)    country = CountrySerializer()    class Meta:        model = AdModel        fields = ['price', 'picture', 'country', 'state', 'color', 'quantity', 'model', 'comment', 'ram', 'memory',                  'seen', 'discount_price', "category", "subcategory"]    def create(self, validated_data):        pictures = validated_data.pop('picture')        country = validated_data.pop('country')        ad = AdModel.objects.create(**validated_data)        for picture in pictures:            AdPictureModel.objects.create(ad=ad, **picture)        CountryModel.objects.create(ad=ad, **country)        return ad    def update(self, instance, validated_data):        pictures = validated_data.pop('picture')        country = validated_data.pop('country')        return super().update(instance, validated_data)class SearchSerializer(serializers.Serializer):    word = serializers.CharField(max_length=255)    class Meta:        fields = "__all__"