from rest_framework import serializersfrom accounts.models import CountryModelfrom apps.ad.models import *from api.v1.account.serializers import CountrySerializerclass AdPictureSerializer(serializers.ModelSerializer):    class Meta:        model = AdPictureModel        fields = '__all__'class AdLocationModelSerializer(serializers.ModelSerializer):    class Meta:        model = AdLocationModel        fields = '__all__'class AdSerializer(serializers.ModelSerializer):    is_liked = serializers.SerializerMethodField("get_is_liked")    class Meta:        model = AdModel        fields = ['id', 'price', 'state', 'color', 'quantity', 'model', 'comment', 'ram', 'memory',                  'seen', 'discount_price', "category", "subcategory", 'is_liked']    def get_is_liked(self, obj):        user = self.context.get("user", False)        if user:            return FavAdModel.objects.filter(ad=obj, owner=user).exists()        return Falseclass SearchSerializer(serializers.Serializer):    word = serializers.CharField(max_length=255)    class Meta:        fields = "__all__"