from rest_framework import serializersfrom apps.ad.models import SubCategoriesModel, CategoriesModelclass CategoriesModelSerializer(serializers.ModelSerializer):    class Meta:        model = CategoriesModel        fields = '__all__'class SubCategoriesModelSerializer(serializers.ModelSerializer):    category = CategoriesModelSerializer()    class Meta:        model = SubCategoriesModel        fields = '__all__'