from rest_framework import serializersfrom apps.ad.models import SubCategoriesModel, CategoriesModelclass CategoriesModelSerializer(serializers.ModelSerializer):    name = serializers.CharField(read_only=True)    class Meta:        model = CategoriesModel        fields = '__all__'class SubCategoriesModelSerializer(serializers.ModelSerializer):    name = serializers.CharField(read_only=True)    class Meta:        model = SubCategoriesModel        exclude = ["category"]