from apps.ad.models import AdLocationModelfrom api.v1.ad.serializers import AdLocationModelSerializerfrom rest_framework.decorators import api_view, permission_classesfrom drf_yasg.utils import swagger_auto_schemafrom rest_framework.response import Responsefrom rest_framework.permissions import IsAuthenticatedfrom rest_framework import statusfrom django.shortcuts import get_object_or_404# @swagger_auto_schema(method="get", tags=["ad-picture"])# @api_view(['GET'])# def get_subcategories_view(request):#     subcategories = AdPictureModel.objects.all()#     serializer = AdPictureSerializer(subcategories, many=True)#     return Response(serializer.data)@swagger_auto_schema(method="get", tags=["ad-location"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_ad_location_view(request, pk):    ad_location = get_object_or_404(AdLocationModel, pk=pk)    serializer = AdLocationModelSerializer(ad_location)    return Response(serializer.data)@swagger_auto_schema(method="post", tags=["ad-location"], request_body=AdLocationModelSerializer)@api_view(['POST'])@permission_classes((IsAuthenticated,))def create_ad_location_view(request):    serializer = AdLocationModelSerializer(data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)# @permission_classes((IsAuthenticated,))# @swagger_auto_schema(method="put", tags=["ad-picture"], request_body=AdPictureSerializer)# @api_view(['PUT'])# def update_ad_picture_view(request, pk):#     fav_ad = get_object_or_404(AdPictureModel, pk=pk)#     serializer = AdPictureSerializer(fav_ad, data=request.data)#     if serializer.is_valid():#         serializer.save()#         return Response(serializer.data, status=status.HTTP_201_CREATED)#     return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="delete", tags=["ad-location"])@api_view(['DELETE'])def delete_ad_location_view(request, pk):    ad_location = get_object_or_404(AdLocationModel, pk=pk)    if ad_location.ad.owner == request.user:        ad_location.delete()        return Response(status=status.HTTP_204_NO_CONTENT)    return Response(status=status.HTTP_401_UNAUTHORIZED)